// Sample data (50 listings)
const districts = ['Hoàn Kiếm','Ba Đình','Cầu Giấy','Đống Đa','Hai Bà Trưng','Thanh Xuân','Nam Từ Liêm','Hà Đông','Tây Hồ','Long Biên','Hoàng Mai','Bắc Từ Liêm'];
const types = ['shophouse','kiosk','office','retail'];
const streets = ['Nguyễn Trãi','Lê Văn Lương','Trần Duy Hưng','Xuân Thủy','Phạm Hùng','Kim Mã','Láng Hạ'];

const data = [];
for(let i=1; i<=50; i++) {
  const district = districts[Math.floor(Math.random()*districts.length)];
  const type = types[Math.floor(Math.random()*types.length)];
  const price = Math.floor(Math.random()*150)+20;
  const area = Math.floor(Math.random()*200)+30;
  const score = Math.floor(Math.random()*40)+50;
  data.push({
    id: 'MB'+String(i).padStart(4,'0'),
    name: `${type.charAt(0).toUpperCase()+type.slice(1)} - ${streets[Math.floor(Math.random()*streets.length)]}`,
    district, type, price, area,
    frontage: Math.round((Math.random()*10+2)*10)/10,
    floors: Math.floor(Math.random()*4)+1,
    latitude: 21.0 + Math.random()*0.05,
    longitude: 105.8 + Math.random()*0.05,
    ai: { potentialScore: score, suggestedPrice: Math.round(price*0.95), riskLevel: score>70?'low':'medium', priceLabel: 'fair' },
    views: Math.floor(Math.random()*2000)+100
  });
}

const query = $input.first().json.query || {};
let filtered = data;
if(query.district) filtered = filtered.filter(x=>x.district===query.district);
if(query.type) filtered = filtered.filter(x=>x.type===query.type);
if(query.maxPrice) filtered = filtered.filter(x=>x.price<=parseInt(query.maxPrice));
const limit = parseInt(query.limit)||20;
filtered = filtered.slice(0,limit);

return [{json:{success:true,count:filtered.length,data:filtered}}];

const stats = {
  total: 1000,
  avgPrice: 67.5,
  avgArea: 85,
  avgPotential: 72,
  byDistrict: {
    'Nam Từ Liêm': 106, 'Hai Bà Trưng': 92, 'Hà Đông': 90,
    'Tây Hồ': 89, 'Hoàng Mai': 87, 'Hoàn Kiếm': 84,
    'Đống Đa': 83, 'Bắc Từ Liêm': 82, 'Thanh Xuân': 77,
    'Ba Đình': 77, 'Long Biên': 68, 'Cầu Giấy': 65
  },
  byType: { shophouse: 283, retail: 247, kiosk: 243, office: 227 }
};
return [{json:{success:true,stats}}];

const body = $input.first().json.body || {};
const { district, area, frontage, floors, type } = body;

const prices = {'Hoàn Kiếm':120,'Ba Đình':100,'Tây Hồ':90,'Cầu Giấy':80,'Đống Đa':75,'Hai Bà Trưng':70,'Thanh Xuân':60,'Nam Từ Liêm':55,'Hà Đông':50,'Long Biên':45,'Hoàng Mai':45,'Bắc Từ Liêm':40};
const base = prices[district] || 50;
let price = base + (area-50)*0.3 + (frontage-4)*5 + (floors-1)*3;
const mult = {shophouse:1.2,office:1.1,retail:1.0,kiosk:0.7};
price *= mult[type]||1;
price = Math.max(10, Math.round(price));

const score = Math.floor(Math.random()*30)+60;
return [{json:{
  success:true,
  valuation: {
    suggestedPrice: price,
    priceRange: {min: Math.round(price*0.9), max: Math.round(price*1.1)},
    potentialScore: score,
    riskLevel: score>75?'low':'medium',
    priceLabel: 'fair',
    factors: [
      {name:'Vị trí',impact:'high',note:district},
      {name:'Diện tích',impact:area>80?'positive':'neutral',note:`${area}m²`},
      {name:'Mặt tiền',impact:frontage>5?'positive':'neutral',note:`${frontage}m`}
    ]
  }
}}];

const body = $input.first().json.body || {};
const { monthlyRent, productPrice, dailyCustomers, operatingCost } = body;
const revenue = productPrice * dailyCustomers * 30;
const cost = monthlyRent + (operatingCost||0);
const profit = revenue - cost;
const breakEven = Math.ceil(monthlyRent / (productPrice * dailyCustomers));
const roi = ((profit/cost)*100).toFixed(1);
return [{json:{
  success:true,
  analysis: {
    monthlyRevenue: revenue,
    totalMonthlyCost: cost,
    monthlyProfit: profit,
    breakEvenDays: breakEven,
    roi: parseFloat(roi),
    recommendation: profit>0 ? 'Có lãi - Khả thi' : 'Lỗ - Cần cân nhắc'
  }
}}];
